con:config.script:
import jxl.*

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

def filePath=groovyUtils.projectPath + "//datadriven.xls"

//log.info(filePath)

Workbook workbook = Workbook.getWorkbook(new File(filePath))
Sheet sheet1 = workbook.getSheet("DataDriven")

def rows = sheet1.getRows()
def cols = sheet1.getColumns()

log.info "Row Count =" + rows 
log.info "Column Count =" + cols 

for(i=1;i<rows;i++) {

    def c1 = sheet1.getCell(0, i)
    def c2 = sheet1.getCell(1, i)
    def sum
    try{
        intA=Integer.parseInt(c1.getContents())
        intB=Integer.parseInt(c2.getContents())
        sum=intA+intB
    }
    catch(Exception e){

    }

    log.info("Before request sum is : "+sum)

    testRunner.testCase.setPropertyValue("Number1",c1.getContents())

    testRunner.testCase.setPropertyValue("Number2",c2.getContents())

    testRunner.runTestStepByName("Add")

    def holder = groovyUtils.getXmlHolder("Add#Response")

    def result = holder["//tem:AddResult"]

    try{
        assert sum==Integer.parseInt(result)
        log.info("Response1 has triggered. And the result is: "+ result)
    }
    catch(Exception e){
        assert "Exception"==result
        log.info("Response2 has triggered. And the result is: "+ result)
    }

}

con:mockOperation.con:dispatchPath:
import com.eviware.soapui.support.GroovyUtils
def goovyUtils= new GroovyUtils(context)
def reqMessage= mockRequest.requestContent

def requestXML= goovyUtils.getXmlHolder(reqMessage)
def intA
def intB
try{
	intA=Integer.parseInt(requestXML.getNodeValue('//tem:intA'))
	intB=Integer.parseInt(requestXML.getNodeValue('//tem:intB'))
	requestContext.sum =  intA + intB
	log.info(intA)
	return "Response 1"
}
catch(Exception e){	
	requestContext.exception =  "Exception"
	return "Response 2"
}
